import OpenAI from "openai";

// the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY || process.env.OPENAI_API_KEY_ENV_VAR || "default_key"
});

interface FlaggedClause {
  title: string;
  summary: string;
  originalText: string;
  riskLevel: 'high' | 'medium' | 'low';
  category: string;
}

interface PolicyAnalysis {
  riskScore: number; // 0-100
  riskLevel: 'high' | 'medium' | 'low';
  summary: string;
  flaggedClauses: FlaggedClause[];
  recommendations: string;
}

export async function analyzePolicy(policyText: string): Promise<PolicyAnalysis> {
  try {
    const prompt = `You are an expert insurance policy analyst. Analyze the following insurance policy text and provide a comprehensive risk assessment.

Policy Text:
${policyText}

Please provide your analysis in JSON format with the following structure:
{
  "riskScore": <number between 0-100, where 100 is highest risk>,
  "riskLevel": <"low", "medium", or "high">,
  "summary": "<2-3 sentence executive summary of the policy's overall risk profile and key characteristics>",
  "flaggedClauses": [
    {
      "title": "<concise title describing the clause concern>",
      "summary": "<plain English explanation of why this clause is concerning or favorable>",
      "originalText": "<exact text from the policy that contains this clause>",
      "riskLevel": "<high, medium, or low>",
      "category": "<type of clause, e.g., 'exclusion', 'limitation', 'coverage', 'deductible'>"
    }
  ],
  "recommendations": "<actionable advice for the policyholder based on the analysis>"
}

Focus on:
1. Pre-existing condition exclusions
2. Waiting periods
3. Coverage limitations
4. Deductible amounts
5. Claim filing requirements
6. Network restrictions
7. Coverage gaps
8. Favorable terms

Provide clear, consumer-friendly explanations that help policyholders understand their coverage.`;

    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: "You are an expert insurance policy analyst. Provide detailed, accurate analysis of insurance policies in JSON format."
        },
        {
          role: "user",
          content: prompt
        }
      ],
      response_format: { type: "json_object" },
      temperature: 0.3, // Lower temperature for more consistent analysis
    });

    const content = response.choices[0].message.content;
    if (!content) {
      throw new Error("No content returned from OpenAI");
    }

    const analysis = JSON.parse(content) as PolicyAnalysis;

    // Validate and sanitize the response
    const sanitizedAnalysis: PolicyAnalysis = {
      riskScore: Math.max(0, Math.min(100, analysis.riskScore || 0)),
      riskLevel: ['low', 'medium', 'high'].includes(analysis.riskLevel) ? analysis.riskLevel : 'medium',
      summary: analysis.summary || "Analysis completed successfully.",
      flaggedClauses: (analysis.flaggedClauses || []).map(clause => ({
        title: clause.title || "Policy Clause",
        summary: clause.summary || "No summary available",
        originalText: clause.originalText || "",
        riskLevel: ['high', 'medium', 'low'].includes(clause.riskLevel) ? clause.riskLevel : 'medium',
        category: clause.category || "general"
      })),
      recommendations: analysis.recommendations || "Please review your policy carefully and consult with an insurance professional if you have questions."
    };

    return sanitizedAnalysis;

  } catch (error) {
    console.error("Error analyzing policy with OpenAI:", error);

    // Return a default analysis if OpenAI fails
    return {
      riskScore: 50,
      riskLevel: 'medium',
      summary: "Unable to complete automated analysis. Please review your policy manually or try uploading again.",
      flaggedClauses: [],
      recommendations: "We recommend having a qualified insurance professional review your policy to identify any potential concerns or coverage gaps."
    };
  }
}

// Alternative function for analyzing specific clauses
export async function analyzeSpecificClause(clauseText: string, context: string): Promise<FlaggedClause> {
  try {
    const prompt = `Analyze this specific insurance policy clause in the context provided.

Clause Text: "${clauseText}"
Context: "${context}"

Provide analysis in JSON format:
{
  "title": "<concise title>",
  "summary": "<plain English explanation>",
  "originalText": "${clauseText}",
  "riskLevel": "<high, medium, or low>",
  "category": "<clause type>"
}`;

    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: "You are an expert insurance policy analyst. Analyze specific clauses and explain their implications in consumer-friendly terms."
        },
        {
          role: "user",
          content: prompt
        }
      ],
      response_format: { type: "json_object" },
      temperature: 0.3,
    });

    const content = response.choices[0].message.content;
    if (!content) {
      throw new Error("No content returned from OpenAI");
    }

    const analysis = JSON.parse(content) as FlaggedClause;
    return analysis;

  } catch (error) {
    console.error("Error analyzing specific clause:", error);
    return {
      title: "Clause Analysis",
      summary: "Unable to analyze this clause automatically.",
      originalText: clauseText,
      riskLevel: 'medium',
      category: 'general'
    };
  }
}

// Function to generate claim preparation guidance
export async function generateClaimGuidance(policyText: string, claimType: string): Promise<string[]> {
  try {
    const prompt = `Based on this insurance policy, provide specific step-by-step guidance for filing a ${claimType} claim.

Policy Text: ${policyText}

Provide guidance as a JSON array of strings, each representing a specific step:
{
  "steps": [
    "Step 1 description",
    "Step 2 description",
    "..."
  ]
}`;

    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: "You are an expert insurance claims specialist. Provide clear, actionable guidance for filing insurance claims."
        },
        {
          role: "user",
          content: prompt
        }
      ],
      response_format: { type: "json_object" },
    });

    const content = response.choices[0].message.content;
    if (!content) {
      return ["Contact your insurance company to begin the claims process."];
    }

    const result = JSON.parse(content);
    return result.steps || ["Contact your insurance company to begin the claims process."];

  } catch (error) {
    console.error("Error generating claim guidance:", error);
    return [
      "Contact your insurance company to report the claim",
      "Gather all relevant documentation",
      "Fill out required claim forms",
      "Submit your claim with supporting evidence",
      "Follow up on claim status regularly"
    ];
  }
}

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { analyzePolicy } from "./openai";
import { yellowSdk } from "./yellowSdk";
import multer from "multer";
import Tesseract from "tesseract.js";
import { z } from "zod";
import { insertPolicySchema, insertClaimSchema } from "@shared/schema";

const upload = multer({ 
  storage: multer.memoryStorage(),
  limits: { fileSize: 10 * 1024 * 1024 } // 10MB limit
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Policy upload and analysis
  app.post('/api/policies/upload', upload.single('policy'), async (req: any, res) => {
    try {
      const userId = 'anonymous-user'; // Use anonymous user instead of authenticated user
      const file = req.file;
      const ipfsHash = req.body.ipfsHash; // IPFS hash from Pinata
      const walletAddress = req.body.walletAddress; // Wallet address from MetaMask

      if (!file) {
        return res.status(400).json({ message: "No file uploaded" });
      }

      // Create policy record
      const policy = await storage.createPolicy({
        userId,
        fileName: file.originalname,
        fileSize: file.size,
        mimeType: file.mimetype,
        ipfsHash: ipfsHash || undefined,
        walletAddress: walletAddress || undefined,
      });

      // Start OCR processing
      res.json({ policyId: policy.id, message: "Upload successful, processing started" });

      // Process OCR in background
      processOCR(policy.id, file.buffer);

    } catch (error) {
      console.error("Error uploading policy:", error);
      res.status(500).json({ message: "Failed to upload policy" });
    }
  });

  async function processOCR(policyId: string, fileBuffer: Buffer) {
    try {
      await storage.updatePolicyAnalysisStatus(policyId, "processing");

      // Extract text using Tesseract
      const { data: { text } } = await Tesseract.recognize(fileBuffer, 'eng');

      await storage.updatePolicyText(policyId, text);

      // Generate policy hash for blockchain verification
      const crypto = require('crypto');
      const policyHash = crypto.createHash('sha256').update(text).digest('hex');

      // Yellow Network Integration: Verify policy on-chain
      let blockchainVerified = false;
      try {
        if (!yellowSdk.isInitialized()) {
          await yellowSdk.initialize();
        }
        blockchainVerified = await yellowSdk.verifyPolicyOnChain(policyHash);
        console.log(`Policy ${policyId} blockchain verification: ${blockchainVerified ? 'SUCCESS' : 'FAILED'}`);
      } catch (yellowError) {
        console.warn('Yellow Network verification failed, continuing with regular analysis:', yellowError);
      }

      // Analyze with AI - Enhanced prompt to include blockchain verification status
      const enhancedText = blockchainVerified 
        ? `${text}\n\n[BLOCKCHAIN VERIFIED: This policy document has been cryptographically verified on Yellow Network's decentralized infrastructure]`
        : text;

      const analysis = await analyzePolicy(enhancedText);

      // Enhance recommendations with blockchain features if verified
      let enhancedRecommendations = analysis.recommendations;
      if (blockchainVerified) {
        enhancedRecommendations += "\n\nðŸ”’ Blockchain Verification: Your policy has been verified on Yellow Network's decentralized infrastructure, ensuring document integrity and authenticity. This provides additional security for your insurance claims and policy management.";
      }

      await storage.createAnalysis({
        policyId,
        riskScore: analysis.riskScore,
        riskLevel: analysis.riskLevel,
        summary: analysis.summary,
        flaggedClauses: analysis.flaggedClauses,
        recommendations: enhancedRecommendations,
      });

      await storage.updatePolicyAnalysisStatus(policyId, "completed");

      // Store document hash securely using Yellow Network
      if (blockchainVerified) {
        try {
          await yellowSdk.storeSecureDocument(policyHash, {
            policyId,
            fileName: `policy_${policyId}`,
            timestamp: Date.now()
          });
        } catch (storageError) {
          console.warn('Yellow Network storage failed:', storageError);
        }
      }

    } catch (error) {
      console.error("Error processing policy:", error);
      await storage.updatePolicyAnalysisStatus(policyId, "failed");
    }
  }

  // Get policy analysis
  app.get('/api/policies/:id/analysis', async (req: any, res) => {
    try {
      const userId = 'anonymous-user';
      const policyId = req.params.id;

      const policy = await storage.getPolicy(policyId);
      if (!policy) {
        return res.status(404).json({ message: "Policy not found" });
      }

      const analysis = await storage.getAnalysisByPolicy(policyId);
      if (!analysis) {
        return res.status(404).json({ message: "Analysis not ready" });
      }

      res.json({ policy, analysis });
    } catch (error) {
      console.error("Error fetching analysis:", error);
      res.status(500).json({ message: "Failed to fetch analysis" });
    }
  });

  // Get user policies
  app.get('/api/policies', async (req: any, res) => {
    try {
      const userId = 'anonymous-user';
      const policies = await storage.getPoliciesByUser(userId);
      res.json(policies);
    } catch (error) {
      console.error("Error fetching policies:", error);
      res.status(500).json({ message: "Failed to fetch policies" });
    }
  });

  // Create claim with Yellow Network integration
  app.post('/api/claims', async (req: any, res) => {
    try {
      const userId = 'anonymous-user';
      const claimNumber = `CM-${Date.now()}-${Math.random().toString(36).substr(2, 4).toUpperCase()}`;
      const claimData = insertClaimSchema.parse({ ...req.body, userId });

      const claim = await storage.createClaim({ ...claimData, claimNumber });

      // Yellow Network Integration: Submit secure claim
      let yellowClaimId = null;
      let blockchainSecured = false;
      try {
        if (!yellowSdk.isInitialized()) {
          await yellowSdk.initialize();
        }

        // Submit claim to Yellow Network's secure channels
        yellowClaimId = await yellowSdk.submitSecureClaim({
          claimId: claim.id,
          claimNumber: claim.claimNumber,
          amount: claim.amount?.toString() || '0',
          description: claim.description,
          policyId: claim.policyId,
          timestamp: Date.now()
        });

        blockchainSecured = true;
        console.log(`Claim ${claim.id} secured on Yellow Network: ${yellowClaimId}`);
      } catch (yellowError) {
        console.warn('Yellow Network claim submission failed, continuing with regular processing:', yellowError);
      }

      // Create enhanced checklist items with blockchain features
      const defaultItems = [
        { 
          title: "Gather Medical Records", 
          description: "Collect all relevant medical documentation from your healthcare providers.", 
          order: 1 
        },
        { 
          title: "Complete Claim Form", 
          description: "Fill out the official claim form with accurate information about your incident.", 
          order: 2 
        },
        { 
          title: "Submit Supporting Evidence", 
          description: "Include photos, receipts, or other documentation that supports your claim.", 
          order: 3 
        },
        { 
          title: "Review Policy Coverage", 
          description: "Verify that your claim falls within your policy coverage limits.", 
          order: 4 
        },
        { 
          title: blockchainSecured ? "Secure Blockchain Submission" : "Submit Claim", 
          description: blockchainSecured 
            ? "Your claim is secured on Yellow Network's decentralized infrastructure for enhanced transparency and faster processing." 
            : "Submit your completed claim with all required documentation.", 
          order: 5 
        },
      ];

      for (const item of defaultItems) {
        await storage.createChecklistItem({
          claimId: claim.id,
          ...item,
        });
      }

      // Create enhanced initial status update
      const initialDescription = blockchainSecured
        ? `Your claim has been successfully submitted and secured on Yellow Network's decentralized infrastructure. Claim ID: ${yellowClaimId || 'pending'}`
        : "Your claim has been successfully submitted and assigned a claim number.";

      await storage.createClaimUpdate({
        claimId: claim.id,
        title: blockchainSecured ? "Claim Secured on Blockchain" : "Claim Submitted",
        description: initialDescription,
        updateType: "status_change",
      });

      // Add blockchain security notification if successful
      if (blockchainSecured) {
        await storage.createClaimUpdate({
          claimId: claim.id,
          title: "Blockchain Security Enabled",
          description: "ðŸ”’ Your claim is now protected by Yellow Network's state channels, ensuring secure processing and transparent tracking throughout the claim lifecycle.",
          updateType: "general_update",
        });
      }

      res.json({ 
        ...claim, 
        yellowClaimId, 
        blockchainSecured,
        securityFeatures: blockchainSecured ? [
          "Decentralized verification",
          "Tamper-proof tracking", 
          "Instant settlement capabilities",
          "Cross-chain payment support"
        ] : []
      });
    } catch (error) {
      console.error("Error creating claim:", error);
      res.status(500).json({ message: "Failed to create claim" });
    }
  });

  // Get user claims
  app.get('/api/claims', async (req: any, res) => {
    try {
      const userId = 'anonymous-user';
      const claims = await storage.getClaimsByUser(userId);
      res.json(claims);
    } catch (error) {
      console.error("Error fetching claims:", error);
      res.status(500).json({ message: "Failed to fetch claims" });
    }
  });

  // Get claim details with checklist and updates
  app.get('/api/claims/:id', async (req: any, res) => {
    try {
      const userId = 'anonymous-user';
      const claimId = req.params.id;

      const claim = await storage.getClaim(claimId);
      if (!claim) {
        return res.status(404).json({ message: "Claim not found" });
      }

      const checklistItems = await storage.getChecklistItemsByClaim(claimId);
      const updates = await storage.getClaimUpdatesByClaim(claimId);

      res.json({ claim, checklistItems, updates });
    } catch (error) {
      console.error("Error fetching claim details:", error);
      res.status(500).json({ message: "Failed to fetch claim details" });
    }
  });

  // Update checklist item
  app.put('/api/checklist/:id', async (req: any, res) => {
    try {
      const itemId = req.params.id;
      const { isCompleted } = req.body;

      await storage.updateChecklistItem(itemId, { isCompleted });
      res.json({ message: "Checklist item updated" });
    } catch (error) {
      console.error("Error updating checklist item:", error);
      res.status(500).json({ message: "Failed to update checklist item" });
    }
  });

  // Policy Comparison Routes
  // Get all policy products
  app.get('/api/policy-products', async (req: any, res) => {
    try {
      const products = await storage.getAllPolicyProducts();
      res.json(products);
    } catch (error) {
      console.error("Error fetching policy products:", error);
      res.status(500).json({ message: "Failed to fetch policy products" });
    }
  });

  // Get policy products by category
  app.get('/api/policy-products/category/:category', async (req: any, res) => {
    try {
      const category = req.params.category;
      const products = await storage.getPolicyProductsByCategory(category);
      res.json(products);
    } catch (error) {
      console.error("Error fetching policy products by category:", error);
      res.status(500).json({ message: "Failed to fetch policy products" });
    }
  });

  // Compare policy - find similar alternatives
  app.get('/api/policies/:id/compare', async (req: any, res) => {
    try {
      const policyId = req.params.id;
      const policy = await storage.getPolicy(policyId);

      if (!policy) {
        return res.status(404).json({ message: "Policy not found" });
      }

      // For demo, assume all uploaded policies are health insurance with 10L coverage
      // In reality, you'd extract this from the policy text or analysis
      const estimatedCoverage = 10; // lakhs
      const category = "health";

      const alternatives = await storage.findSimilarPolicies(estimatedCoverage, category);

      res.json({
        current: {
          id: policy.id,
          fileName: policy.fileName,
          estimatedCoverage,
          category
        },
        alternatives,
        comparisonDate: new Date().toISOString()
      });
    } catch (error) {
      console.error("Error comparing policy:", error);
      res.status(500).json({ message: "Failed to compare policy" });
    }
  });

  // Get detailed comparison between policies
  app.post('/api/policies/compare-detailed', async (req: any, res) => {
    try {
      const { policyIds } = req.body;

      if (!Array.isArray(policyIds) || policyIds.length === 0) {
        return res.status(400).json({ message: "Policy IDs array is required" });
      }

      const policies = [];
      for (const id of policyIds) {
        const policy = await storage.getAllPolicyProducts().then(products => 
          products.find(p => p.id === id)
        );
        if (policy) {
          policies.push(policy);
        }
      }

      res.json({
        policies,
        comparisonMetrics: {
          coverageRange: {
            min: Math.min(...policies.map(p => p.coverage)),
            max: Math.max(...policies.map(p => p.coverage))
          },
          premiumRange: {
            min: Math.min(...policies.map(p => p.premium)),
            max: Math.max(...policies.map(p => p.premium))
          },
          bestClaimRatio: Math.max(...policies.map(p => p.claimSettlementRatio || 0)),
          shortestWaitingPeriod: Math.min(...policies.map(p => p.waitingPeriod || 0))
        }
      });
    } catch (error) {
      console.error("Error in detailed comparison:", error);
      res.status(500).json({ message: "Failed to perform detailed comparison" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

